def fix(arr, ticker):
    formatted_news = {}
    pattern_news_title = '(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)-..-..\s.*'
    pattern_key_fn = '\d\d:\d\d(AM|PM)'
    pattern_news_content = '\d\d:\d\d(AM|PM)\s.*'
    for i in range(len(arr)):
        new = {}
        if re.match(pattern_news_title, news[i]):
            formatted_news[ ticker + re.split(pattern_key_fn, news[i])[0].replace('-','') ] = 1
        if re.match(pattern_news_content, news[i]):
            title = news[i].split(pattern_key_fn)[0]
            new['title'] = re.split(pattern_key_fn, title)[2].strip()
        print(new)
    print(formatted_news)

fix(news, 'ASAP')